<?phpglobal $porto_settings;?><div class="panel-overlay"></div><div id="nav-panel" class="<?php echo (isset($porto_settings['mobile-panel-pos']) && $porto_settings['mobile-panel-pos']) ? $porto_settings['mobile-panel-pos'] : '' ?>">    <?php    // show currency and view switcher    $switcher = '';    $switcher .= porto_mobile_currency_switcher();    $switcher .= porto_mobile_view_switcher();    if ($switcher)        echo '<div class="switcher-wrap">' . $switcher . '</div>';    // show top navigation and mobile menu    $menu = porto_mobile_menu();    if ($menu)        echo '<div class="menu-wrap">' . $menu . '</div>';    $header_type = porto_get_header_type();    $menu = porto_mobile_top_navigation();    if ($menu)        echo '<div class="menu-wrap">' . $menu . '</div>';    // show social links    echo porto_header_socials();    ?></div><a href="#" id="nav-panel-close"   class="<?php echo (isset($porto_settings['mobile-panel-pos']) && $porto_settings['mobile-panel-pos']) ? $porto_settings['mobile-panel-pos'] : '' ?>"><i        class="fa fa-close"></i></a>